def hlp(screen):
    scenes = []
    effects = [
        Print(screen,
              ColourImageFile(screen, "./gif/help.gif", screen.height,
                              uni=screen.unicode_aware),
              screen.height//- 5,
              speed=1),
    ]
    scenes.append(Scene(effects, 24))

    screen.play(scenes, stop_on_resize=False, repeat=False)
